import Web3 from 'web3'

import baseContractABI from '../abi/base_contract_abi.json'
import { validateAddress } from './validate.js'
import { assertAdmin } from './permission_assert.js'

/**
 * BaseContract contains common methods for all contracts of a KyberNetwork
 * reserve.
 */
export default class BaseContract {
  /**
   * Create new BaseContract instance.
   * @param {object} web3 - Web3 instance
   * @param {string} address - address of smart contract.
   */
  constructor (web3, address) {
    if (!web3) {
      throw new Error('missing web3 instance')
    }

    if (web3.currentProvider == null ) {
      throw new Error('web3 instance has no provider')
    } 

    validateAddress(address)

    this.web3 = web3
    this.contract = new web3.eth.Contract(baseContractABI, address)
  }

  /**
   * Return the current admin address of contract.
   * @return {string} - current admin address
   */
  admin () {
    return this.contract.methods.admin().call()
  }

  /**
   * Return the pending admin address of contract.
   * An admin address is placed in pending if it is transfered but hasn't been
   * claimed yet.
   * @return {string} - pending admin address
   */
  pendingAdmin () {
    return this.contract.methods.pendingAdmin().call()
  }

  /**
   * transfer admin privilege to given address.
   * @param {object} adminAddress - current admin account
   * @param {string} address - new admin address
   * @param {number} gasPrice (optional) - the gasPrice desired for the tx
   */
  async transferAdmin (adminAddress, address, gasPrice) {
    validateAddress(address)
    await assertAdmin(this, adminAddress)
    const med = this.contract.methods.transferAdmin(address)
    return med.send({
      from: adminAddress,
      gas: await med.estimateGas({
        from: adminAddress,
        gasPrice: gasPrice
      })
    })
  }

  /**
   * Claim admin privilege. The account address should be in already placed
   * in pendingAdmin for this to works.
   * @param {object} newAccount - the pending admin account
   * @param {number} gasPrice (optional) - the gasPrice desired for the tx
   */
  async claimAdmin (newAccount, gasPrice) {
    const med = this.contract.methods.claimAdmin()
    return med.send({
      from: newAccount,
      gas: await med.estimateGas({
        from: newAccount,
        gasPrice: gasPrice
      })
    })
  }

  /**
   * Return operator addresses of contract.
   * @return {array} - list of operator addresses
   */
  getOperators () {
    return this.contract.methods.getOperators().call()
  }

  /**
   * Add given address from operators list.
   * @param {object} adminAddress - current admin account
   * @param {string} address - address to remove from operators list.
   * @param {number} gasPrice (optional) - the gasPrice desired for the tx
   */
  async addOperator (adminAddress, address, gasPrice) {
    validateAddress(address)
    await assertAdmin(this, adminAddress)
    const med = this.contract.methods.addOperator(address)
    return med.send({
      from: adminAddress,
      gas: await med.estimateGas({
        from: adminAddress,
        gasPrice: gasPrice
      })
    })
  }

  /**
   * Remove given address from operators list.
   * @param {object} adminAddress - current admin account
   * @param {string} address - address to remove from operators list.
   * @param {number} gasPrice (optional) - the gasPrice desired for the tx
   */
  async removeOperator (adminAddress, address, gasPrice) {
    validateAddress(address)
    await assertAdmin(this, adminAddress)
    const med = this.contract.methods.removeOperator(address)
    return med.send({
      from: adminAddress,
      gas: await med.estimateGas({
        from: adminAddress,
        gasPrice: gasPrice
      })
    })
  }

  /**
   * Return alerter addresses of contract.
   * @return {array} - list of alerter addresses
   */
  getAlerters () {
    return this.contract.methods.getAlerters().call()
  }

  /**
   * Add new address to alerters list.
   * @param {object} adminAddress - current admin account
   * @param {string} address - address to add to alerters list.
   * @param {number} gasPrice (string) - the gasPrice desired for the tx
   */
  async addAlerter (adminAddress, address, gasPrice) {
    validateAddress(address)
    await assertAdmin(this, adminAddress)
    const med = this.contract.methods.addAlerter(address)
    return med.send({
      from: adminAddress,
      gas: await med.estimateGas({
        from: adminAddress,
        gasPrice: gasPrice
      })
    })
  }

  /**
   * Remove address from alerters list.
   * @param {object} adminAddress - current admin account
   * @param {string} address - address to remove from alerters list.
   * @param {number} gasPrice (string) - the gasPrice desired for the tx
   */
  async removeAlerter (adminAddress, address, gasPrice) {
    validateAddress(address)
    await assertAdmin(this, adminAddress)
    const med = this.contract.methods.removeAlerter(address)
    return med.send({
      from: adminAddress,
      gas: await med.estimateGas({
        from: adminAddress,
        gasPrice: gasPrice
      })
    })
  }
}

import Web3 from 'web3'
import BigNumber from 'bignumber.js'

import BaseContract from './base_contract.js'
import conversionRatesABI from '../abi/ConversionRatesContract.abi.json'
import { validateAddress } from './validate.js'
import { assertOperator, assertAdmin } from './permission_assert.js'

/**
 * CompactData is used to save gas on get, set rates operations.
 * Instead of sending the whole buy, sell rates every time, only the different
 * is sent if possible.
 * The compact rate is calculated by following formula:
 *
 *      compact = ((rate / base) - 1) * 1000
 */
export class CompactData {
  get isBaseChanged () {
    return this._isBaseChanged
  }

  set isBaseChanged (value) {
    this._isBaseChanged = value
  }

  get compact () {
    return this._compact
  }

  set compact (value) {
    this._compact = value
  }

  get base () {
    return this._base
  }

  set base (value) {
    this._base = value
  }

  /**
   * Create a new CompactData instance.
   * @param {BigNumber} rate - the total rate
   * @param {BigNumber} base - the base to generate compact data
   */
  constructor (rate, base) {
    // const compactData = buildCompactData(rate, base)
    const minInt8 = -128
    const maxInt8 = 127

    rate = new BigNumber(rate)
    base = new BigNumber(base)

    let compact

    if (base.isEqualTo(0)) {
      base = rate
      this.isBaseChanged = true
      compact = new BigNumber(0)
    } else {
      compact = rate
        .dividedBy(base)
        .minus(new BigNumber(1))
        .multipliedBy(1000.0)
        .integerValue()

      // compact data is fit in a byte
      if (
        compact.isGreaterThanOrEqualTo(minInt8) &&
        compact.isLessThanOrEqualTo(maxInt8)
      ) {
        // overflowed, convert from int8 to byte so
        // * -1 --> 255
        // * -128 --> 128
        if (compact.isLessThan(0)) {
          compact = new BigNumber(2 ** 8).plus(compact)
        }
      } else {
        base = rate
        this.isBaseChanged = true
        compact = new BigNumber(0)
      }
    }

    this._base = base
    this._compact = compact
  }
}

/**
 * Build the compact data input.
 * In ConversionRates contract, the compact data is stored in two dimensions
 * array with location:
 *   - bulkIndex
 *   - indexInBulk
 *
 * When setting compact data, user needs to submit the whole bulk along with
 * its index.
 *
 * @param newBuys - buy compact data
 * @param newSells - sell compact data
 * @param indices - map of address to its bulk index
 * @return {{buyResults: Array, sellResults: Array, indexResults: Array}}
 */
export const buildCompactBulk = (newBuys, newSells, indices) => {
  let buyResults = []
  let sellResults = []
  let indexResults = []
  let buyBulks = {}
  let sellBulks = {}

  for (let addr in newBuys) {
    if (newBuys.hasOwnProperty(addr)) {
      const loc = indices[addr]
      if (!(loc.bulkIndex in buyBulks)) {
        buyBulks[loc.bulkIndex] = new Array(14).fill(0)
        sellBulks[loc.bulkIndex] = new Array(14).fill(0)
      }
      buyBulks[loc.bulkIndex][loc.indexInBulk] = newBuys[addr]
      sellBulks[loc.bulkIndex][loc.indexInBulk] = newSells[addr]
    }
  }

  for (let i in buyBulks) {
    if (buyBulks.hasOwnProperty(i)) {
      buyResults.push(buyBulks[i])
      sellResults.push(sellBulks[i])
      indexResults.push(i)
    }
  }
  return { buyResults, sellResults, indexResults }
}

/**
 * TokenControlInfo is the configurations of a ERC20 token.
 */
export class TokenControlInfo {
  get maxTotalImbalance () {
    return this._maxTotalImbalance
  }

  set maxTotalImbalance (value) {
    this._maxTotalImbalance = value
  }

  get maxPerBlockImbalance () {
    return this._maxPerBlockImbalance
  }

  set maxPerBlockImbalance (value) {
    this._maxPerBlockImbalance = value
  }

  get minimalRecordResolution () {
    return this._minimalRecordResolution
  }

  set minimalRecordResolution (value) {
    this._minimalRecordResolution = value
  }

  /**
   * Create a new TokenControlInfo instance.
   * @param minimalRecordResolution {uint} - minimum denominator in token wei that can be changed
   * @param maxPerBlockImbalance {uint} - maximum wei amount of net absolute (+/-) change for a token in an ethereum
   * block
   * @param maxTotalImbalance {uint} - wei amount of the maximum net token change allowable that happens between 2
   * price updates
   */
  constructor (
    minimalRecordResolution,
    maxPerBlockImbalance,
    maxTotalImbalance
  ) {
    this._minimalRecordResolution = minimalRecordResolution
    this._maxPerBlockImbalance = maxPerBlockImbalance
    this._maxTotalImbalance = maxTotalImbalance
  }
}

